mapper.select:
SELECT REPLY_ID, REPLY_CONTENT, REPLY_WRITER, REPLY_REGISTER_DATE, REPLY_UPDATE_DATE, BOARD_ID FROM ( SELECT ROWNUM R, REPLY_ID, REPLY_CONTENT, REPLY_WRITER, REPLY_REGISTER_DATE, REPLY_UPDATE_DATE, BOARD_ID FROM ( SELECT REPLY_ID, REPLY_CONTENT, REPLY_WRITER, REPLY_REGISTER_DATE, REPLY_UPDATE_DATE, BOARD_ID FROM TBL_REPLY WHERE BOARD_ID = ${boardId} ORDER BY REPLY_ID DESC )
WHERE ROWNUM <= #{page} * #{rowCount}
)
WHERE R > ((#{page} - 1) * #{rowCount})

mapper.update:
UPDATE TBL_REPLY
SET REPLY_CONTENT = #{replyContent},REPLY_UPDATE_DATE = SYSDATE
WHERE REPLY_ID = #{replyId}
